import React, { Component } from 'react'
import posed from 'react-pose';


const Animation = posed.div({
    visible : {
        opacity : 1,
        applyAtStart : {
            display : "block"
        }
    },
    hidden : {
        opacity : 0,
        applyAtEnd : {
            display : "none"
        }
    }
});

class AddUser extends Component {

    state = {
        visible : false,
        name : "",
        department : "",
        salary : ""
    }

    changeVisiblity = (e) => {
        this.setState({
            visible : !this.state.visible
        })
    }

    changeName = (e) => {
        this.setState({
            name : e.target.value
        })

    }

    changeDepartment = (e) => {

    }

    changeSalary = (e) => {

    }

    render() {

        const {visible, name, salary, department} = this.state;
        return (
            <div className = "col-md-8 mb-4">

                <button onClick = {this.changeVisiblity} className = "btn btn-dark btn-block bm-1">{visible ? "Hide Form" : "Show Form"}</button>

                <Animation pose = {visible ? "visible" : "hidden"}>

                <div className = "card">
                    <div className = "card-header">
                        <h4>Add User Form</h4>
                    </div>

                    <div className = "card-body">
                        <form>
                            <div className = "from-group">
                                <label htmlFor = "name">Name</label>
                                <input
                                type = "text"
                                name = "name"
                                id = "id"
                                placeholder = "Enter name"
                                className = "form-control"
                                value = {name}
                                onChange = {this.changeName}

                                />
                            </div>

                            <div className = "from-group">
                                <label htmlFor = "department">Department</label>
                                <input
                                type = "text"
                                name = "department"
                                id = "department"
                                placeholder = "Enter department"
                                className = "form-control"
                                value = {department}
                                onChange = {this.changeDepartment}

                                />
                            </div>

                            <div className = "from-group">
                                <label htmlFor = "salary">Salary</label>
                                <input
                                type = "text"
                                name = "salary"
                                id = "id"
                                placeholder = "Enter salary"
                                className = "form-control"
                                value = {salary}
                                onChange = {this.changeSalary}

                                />
                            </div>

                            <button className = "btn btn-danger btn-block mt-3" type = "submit">Add</button>
                        </form>
                    </div>
                </div>
                </Animation>
            </div>
        )
    }
}
export default AddUser;
