import React, { Component } from 'react'
import PropTypes from 'prop-types'


class User extends Component {
    //bind etmek için ilk yöntem: fonksiyon tanımlanır sonra bind edilir 
    //örnekte kullandığımız yöntem de bu
    /*
    onClickEvet(e){
